mapper.select:
SELECT 
FROM mall_cart
WHERE cart_user_id = #{userId}
And cart_product_id = #{productId}

mapper.delete:
DELETE FROM mall_cart
WHERE cart_user_id = #{userId}

  AND cart_product_id in

    #{item}

mapper.update:
UPDATE mall_cart
SET cart_checked = #{checked},
cart_update_time = now()
WHERE cart_user_id = #{userId}

  AND cart_product_id = #{productId}

mapper.select:
SELECT IFNULL(sum(cart_quantity), 0) AS COUNT
FROM mall_cart
WHERE cart_user_id = #{userId}

SELECT 
FROM mall_cart
WHERE cart_user_id = #{userId}
AND cart_checked = 1;
