mapper.select:
select

FROM mall_user
WHERE user_name = #{username}
and user_password = #{password}

select user_question
FROM mall_user
WHERE user_name = #{username}

select count(1)
FROM mall_user
WHERE user_name = #{username}
and user_question = #{question}
and user_answer = #{answer}

mapper.update:
update mall_user
SET user_password = #{passwordNew},user_update_time = now()
WHERE user_name = #{username}

mapper.select:
select count(1)
FROM mall_user
WHERE user_password = #{password}
and user_id = #{userId}

select count(1)
FROM mall_user
WHERE user_email = #{email}
and user_id != #{userId}
